import React, { useState, useEffect } from "react";
import axios from "axios";

const Features = () => {
  const [activeFeature, setActiveFeature] = useState("featureA");
  const [cards, setCards] = useState([]);

  const featuresData = {
    featureA: [
      { title: "A1", description: "Feature A - Card 1", image: "", price: 10 },
      { title: "A2", description: "Feature A - Card 2", image: "", price: 15 },
    ],
    featureB: [
      { title: "B1", description: "Feature B - Card 1", image: "", price: 12 },
      { title: "B2", description: "Feature B - Card 2", image: "", price: 18 },
    ],
    featureC: [
      { title: "C1", description: "Feature C - Card 1", image: "", price: 20 },
      { title: "C2", description: "Feature C - Card 2", image: "", price: 22 },
    ],
    featureD: [
      { title: "D1", description: "Feature D - Card 1", image: "", price: 25 },
      { title: "D2", description: "Feature D - Card 2", image: "", price: 30 },
    ],
  };

  const handleFeatureClick = async (featureKey) => {
    setActiveFeature(featureKey);
    const FeaturesId = featureKey;

    try {
      // Insert feature cards (optional step)
      await Promise.all(
        featuresData[featureKey].map((card) =>
          axios.post("http://localhost:5000/api/insert-feature", {
            FeaturesId,
            ...card,
          })
        )
      );
    } catch (err) {
      console.log("Insert error (may already exist):", err.message);
    }

    try {
      // Fetch feature cards
      const res = await axios.get(
        `http://localhost:5000/api/fetch-feature/${FeaturesId}`
      );
      setCards(res.data);
    } catch (err) {
      console.error("Fetch error:", err.message);
    }
  };

  useEffect(() => {
    handleFeatureClick(activeFeature);
  }, []);

  return (
    <div className="p-6">
      <div className="flex space-x-4 mb-6">
        {Object.keys(featuresData).map((featureKey) => (
          <button
            key={featureKey}
            onClick={() => handleFeatureClick(featureKey)}
            className={`px-4 py-2 rounded-lg ${
              activeFeature === featureKey
                ? "bg-blue-500 text-white"
                : "bg-gray-200"
            }`}
          >
            {featureKey.toUpperCase()}
          </button>
        ))}
      </div>

      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {cards.map((card, index) => (
          <div
            key={index}
            className="bg-white rounded-xl p-4 shadow-md hover:shadow-xl transition-all"
          >
            <h3 className="text-xl font-bold">{card.title}</h3>
            <p className="text-gray-600">{card.description}</p>
            <p className="text-green-600">Price: ${card.price}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Features;
