import React, { useState } from "react";

const featuresData = {
  featureA: [
    { id: 1, title: "A1", description: "Feature A - Card 1" },
    { id: 2, title: "A2", description: "Feature A - Card 2" },
    { id: 3, title: "A3", description: "Feature A - Card 3" },
    { id: 4, title: "A4", description: "Feature A - Card 4" },
    { id: 5, title: "A5", description: "Feature A - Card 5" },
  ],
  featureB: [
    { id: 1, title: "B1", description: "Feature B - Card 1" },
    { id: 2, title: "B2", description: "Feature B - Card 2" },
    { id: 3, title: "B3", description: "Feature B - Card 3" },
    { id: 4, title: "B4", description: "Feature B - Card 4" },
    { id: 5, title: "B5", description: "Feature B - Card 5" },
  ],
  featureC: [
    { id: 1, title: "C1", description: "Feature C - Card 1" },
    { id: 2, title: "C2", description: "Feature C - Card 2" },
    { id: 3, title: "C3", description: "Feature C - Card 3" },
    { id: 4, title: "C4", description: "Feature C - Card 4" },
    { id: 5, title: "C5", description: "Feature C - Card 5" },
  ],
  featureD: [
    { id: 1, title: "D1", description: "Feature D - Card 1" },
    { id: 2, title: "D2", description: "Feature D - Card 2" },
    { id: 3, title: "D3", description: "Feature D - Card 3" },
    { id: 4, title: "D4", description: "Feature D - Card 4" },
    { id: 5, title: "D5", description: "Feature D - Card 5" },
  ],
};

const Feautres = () => {

  const [activeFeature, setActiveFeature] = useState("featureA");

  const handleFeatureClick = (featureKey) => {
    setActiveFeature(featureKey);
  };

  return (
    <div className="p-6">
      {/* Feature Tabs */}
      <div className="flex space-x-4 mb-6">
        {Object.keys(featuresData).map((featureKey) => (
            console.log(featureKey),
          <button
            key={featureKey}
            onClick={() => handleFeatureClick(featureKey)}
            className={`px-4 py-2 rounded-lg ${
              activeFeature === featureKey
                ? "bg-blue-500 text-white"
                : "bg-gray-200"
            }`}
          >
            {featureKey.toUpperCase()}
          </button>
        ))}
      </div>

      {/* Cards Display */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4">
        {featuresData[activeFeature].map((card) => (
          <div
            key={card.id}
            className="bg-white rounded-xl p-4 shadow-md hover:shadow-xl transition-all"
          >
            <h3 className="text-xl font-bold">{card.title}</h3>
            <p className="text-gray-600">{card.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
}

export default Feautres;
